void doTheSlashAnalysis(StringPtr theTempVol);void doTheSlashAnalysis(StringPtr theTempVol){	short z;		for (z=1;z<*theTempVol+1;z++)	{		if (*((char *)(theTempVol + z)) == '/')		{					*((char *)(theTempVol + z)) = '|';					}				if (*((char *)(theTempVol + z)) == '(')		{					*((char *)(theTempVol + z)) = '|';					}				if (*((char *)(theTempVol + z)) == ')')		{					*((char *)(theTempVol + z)) = '|';					}	}}